import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './login.css';

const SignupApp = () => {
  const navigate = useNavigate();
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [signUpStatus, setSignUpStatus] = useState('');
  const navigateToSignup = () => navigate('/signup');

  const handleSignUp = async (event) => {
    event.preventDefault();
    setSignUpStatus('Logining up...');

    try {
      const response = await fetch('http://localhost:3001/signup', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ username, password }),
      });

      if (!response.ok) {
        if (response.status === 409) {
          throw new Error('User already exists');
        }
        throw new Error(`Login failed: ${response.status}`);
      }

      setSignUpStatus('Login successful!');
      navigate('/dashboard'); // Redirects to the dashboard
    } catch (error) {
      setSignUpStatus(error.message);
    }
  };

  return (
    <div className="app">
      <div className="illustration" />
      <div className="form-container">
        <form className="login-form" onSubmit={handleSignUp}>
          <div className="input-group">
            <label htmlFor="username">USERNAME</label>
            <input
              type="text"
              id="username"
              name="username"
              value={username}
              onChange={e => setUsername(e.target.value)}
            />
          </div>
          <div className="input-group">
            <label htmlFor="password">PASSWORD</label>
            <input
              type="password"
              id="password"
              name="password"
              value={password}
              onChange={e => setPassword(e.target.value)}
            />
          </div>
          <button type="submit">Log in</button>
          <button type="button" onClick={navigateToSignup} className="decline-btn">
      Create a new account
    </button>
        </form>
        {signUpStatus && <p className="signup-status">{signUpStatus}</p>}
      </div>
    </div>
  );
};

export default SignupApp;
