import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';

import './SignupApp.css';
 // Apne App.css file me styles ensure karein
const history = useHistory();

const App = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [signUpStatus, setSignUpStatus] = useState('');

  const handleSignUp = (event) => {
    event.preventDefault();
    setSignUpStatus('Signing up...');

    fetch('http://localhost:3001/signup', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ username, password }),
    })
    .then(response => {
      if (!response.ok) {
        if (response.status === 409) {
          throw new Error('User already exists');
        }
        throw new Error(`Signup failed: ${response.status}`);
      }
      return response.text();
    })
    .then(data => {
      setSignUpStatus('Signup successful!');
      history.push('/dashboard'); // Yahaan aapko redirect URL daalna hai, jaise ki '/dashboard' ya '/tasks' jo bhi aap chahein.
    })
    
    .catch(error => {
      setSignUpStatus(error.message);
    });
  };

  return (
    <div className="app">
      <div className="illustration" />
      <div className="form-container">
        <form className="signup-form" onSubmit={handleSignUp}>
          <div className="input-group">
            <label htmlFor="username">USERNAME</label>
            <input
              type="text"
              id="username"
              name="username"
              value={username}
              onChange={e => setUsername(e.target.value)}
            />
          </div>
          <div className="input-group">
            <label htmlFor="password">PASSWORD</label>
            <input
              type="password"
              id="password"
              name="password"
              value={password}
              onChange={e => setPassword(e.target.value)}
            />
          </div>
          <button type="submit">SIGN UP</button>
          <button type="button" className="decline-btn">NO THANKS</button>
        </form>
        {signUpStatus && <p className="signup-status">{signUpStatus}</p>}
      </div>
    </div>
  );
};

export default App;
