import React, { useState, useEffect } from 'react';
import './Teams.css';

const Teams = () => {
  const [messages, setMessages] = useState([]);
  const [files, setFiles] = useState([]);
  const [newMessage, setNewMessage] = useState('');

  useEffect(() => {
    // Fetch messages when the component mounts
    const fetchMessages = async () => {
      try {
        const response = await fetch('http://localhost:3001/getMessages');
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        const data = await response.json();
        setMessages(data);
      } catch (error) {
        console.error("Could not fetch messages:", error);
      }
    };

    fetchMessages();
  }, []);

  const handleSend = async () => {
    if (newMessage.trim() !== '' || files.length > 0) {
      const fileAttachments = files.map(file => ({
        name: file.name,
        size: file.size,
        url: file.url // Make sure this is the correct URL that the server can handle
      }));

      const messageData = {
        author: 'You',
        content: newMessage,
        files: fileAttachments, // Include file details
        type: 'sent'
      };

      try {
        const response = await fetch('http://localhost:3001/storeMessage', {
          method: 'POST',
          headers: {
              'Content-Type': 'application/json',
          },
          body: JSON.stringify(messageData),
        });

        const result = await response.json();
        if (response.ok) {
          // Add the new message to the local state
          setMessages(prevMessages => [...prevMessages, result]); // Assuming the server sends back the stored message
          setNewMessage('');
          setFiles([]); // Clear files after sending
        } else {
          throw new Error(result.message);
        }
      } catch (error) {
          console.error('Error:', error);
      }
    }
  };

  const handleFileUpload = (event) => {
    const uploadedFiles = event.target.files;
    const fileList = Array.from(uploadedFiles).map((file) => ({
      name: file.name,
      size: file.size,
      url: URL.createObjectURL(file), // This URL is for local representation only, not for server storage
    }));
    setFiles([...files, ...fileList]);
  };

  const handleReply = (author) => {
    setNewMessage(`Replying to ${author}: `);
  };

  return (
    <div className="chat-container">
      {/* ...other parts of your component... */}
      
      <div className="chat-input">
        <input
          type="text"
          placeholder="Type something..."
          value={newMessage}
          onChange={(e) => setNewMessage(e.target.value)}
          onKeyPress={(e) => e.key === 'Enter' && handleSend()} // Here we use handleSend
        />
        <button className="send-btn" onClick={handleSend}>Send</button> {/* Here we use handleSend */}
      </div>
      <input
        id="fileUpload"
        type="file"
        multiple
        onChange={handleFileUpload} // Here we use handleFileUpload
        style={{ display: 'none' }}
      />
      <div className="tab" onClick={() => document.getElementById('fileUpload').click()}>Files</div>
      
      {/* ...rest of your component... */}
    </div>
  );
};

export default Teams;
